import React, { Component } from 'react';
import '../../css/Register.css';
import { Link } from 'react-router-dom';


export default class Register extends Component {

    solo = () =>{
      var team = document.getElementById('Team');
      team.style.display="none";
      var solo = document.getElementById('Solo');
      solo.style.display="block";
      var soloButton = document.getElementById('soloButton');
      soloButton.classList.add('active');
      var teamButton = document.getElementById('teamButton');
      teamButton.classList.remove('active');
    }
    team = () =>{
      var x = document.getElementById('Solo');
      x.style.display="none";
      var team = document.getElementById('Team');
      team.style.display="block";
      var soloButton = document.getElementById('soloButton');
      soloButton.classList.remove('active');
      var teamButton = document.getElementById('teamButton');
      teamButton.classList.add('active');
    }
    mySubmitHandler = (event) =>{
      event.preventDefault();

    }

  render() {
        return (
            <React.Fragment>
            <div class="godown"/>
            <div class="regbody"/>
            <form onSubmit={this.mySubmitHandler}>

              <label>Team Name</label>
              <i class='fas fa-user'></i>
              <input type="text" placeholder="Team Name"/><br/>
              <label>Password</label>
              <input type="password" placeholder="******"/><br/>
              <label>Conform Password</label>
              <input type="password" placeholder="*******"/><br/>

              <button onClick={this.solo} id="soloButton">Solo</button><button onClick={this.team} id="teamButton">Team</button>

              <div id="Solo">
                <label>Full Name</label>
                <input type="text" name="Member1" placeholder="Name"/><br/>
                <label>Email</label>
                <input type="email" name="StudentId" placeholder="00000000@lcps.org"/><br/>
              </div>
              <div id="Team">
                <label>Team Member 1</label>
                <input type="text" name="Member1" placeholder="Full Name"/><br/>
                <label>Email</label>
                <input type="email" name="StudentId1" placeholder="00000000@lcps.org"/><br/>
                <label>Team Member 2</label>
                <input type="text" name="Member2" placeholder="Full Name"/><br/>
                <label>Email</label>
                <input type="email" name="StudentId2" placeholder="00000000@lcps.org"/><br/>
                <label>Team Member 3</label>
                <input type="text" name="Member3" placeholder="Full Name"/><br/>
                <label>Email</label>
                <input type="email" name="StudentId3" placeholder="00000000@lcps.org"/>  <br/>

              </div>
            </form>
    </React.Fragment>
        )
    }
}

const paddingStyle ={
    padding:'8px'
}
